server/adminAuth.ts.content:
import { Request, Response, NextFunction } from 'express';

// Simple admin authentication middleware
export interface AdminSession {
  isAdmin: boolean;
  loginTime: Date;
}

// Extend Express Request to include admin session
declare global {
  namespace Express {
    interface Request {
      adminSession?: AdminSession;
    }
  }
}

// Default admin credentials
const ADMIN_CREDENTIALS = [
  { username: 'admin', password: '12345' },
  { username: 'superadmin', password: '12345' }
];

export function initializeAdminAuth(app: any) {
  // Admin login endpoint
  app.post('/api/admin/login', (req: Request, res: Response) => {
    const { username, password } = req.body;

    const validCredentials = ADMIN_CREDENTIALS.find(cred => cred.username === username && cred.password === password);

    if (validCredentials) {
      // Set admin session
      req.session.adminSession = {
        isAdmin: true,
        loginTime: new Date()
      };

      res.json({ success: true, message: 'Admin login successful' });
    } else {
      res.status(401).json({ success: false, message: 'Invalid credentials' });
    }
  });

  // Admin logout endpoint
  app.post('/api/admin/logout', (req: Request, res: Response) => {
    req.session.adminSession = undefined;
    res.json({ success: true, message: 'Admin logged out' });
  });

  // Check admin status endpoint
  app.get('/api/admin/status', (req: Request, res: Response) => {
    const isAdmin = req.session.adminSession?.isAdmin || false;
    res.json({ isAdmin });
  });
}

// Admin authentication middleware
export function requireAdmin(req: Request, res: Response, next: NextFunction) {
  if (req.session.adminSession?.isAdmin) {
    next();
  } else {
    res.status(403).json({ error: 'Admin access required' });
  }
}

server/db.ts.content:
import mysql from "mysql2/promise";
import { drizzle } from "drizzle-orm/mysql2";
import * as schema from "@shared/schema";

// Check for MySQL database URL, fallback to environment variable
const databaseUrl = process.env.MYSQL_DATABASE_URL || process.env.DATABASE_URL;

if (!databaseUrl) {
  throw new Error("MYSQL_DATABASE_URL or DATABASE_URL must be set.");
}

// Create MySQL connection pool  
const pool = mysql.createPool({
  uri: databaseUrl,
  waitForConnections: true,
  connectionLimit: 10,
});

// Create Drizzle client using the schema with mode
export const db = drizzle(pool, { schema, mode: "default" });

