    const updatedProduct: Product = {
      ...existingProduct,
      ...updates,
      status: updates.status ?? existingProduct.status,
      isApproved: updates.isApproved ?? existingProduct.isApproved,
      approvalDate: updates.approvalDate ?? existingProduct.approvalDate,
