  async createCommunityPost(
    insertPost: InsertCommunityPost,
  ): Promise<CommunityPost> {
    const [post] = await db
      .insert(communityPosts)
      .values(insertPost)
      .returning();
    return post;
  }

  async likeCommunityPost(id: number): Promise<CommunityPost | undefined> {
    const [post] = await db
      .update(communityPosts)
      .set({ likes: sql`${communityPosts.likes} + 1` })
      .where(eq(communityPosts.id, id))
      .returning();
    return post;
  }

  async getCommunityComments(postId: number): Promise<CommunityComment[]> {
    return await db
      .select()
      .from(communityComments)
      .where(eq(communityComments.postId, postId));
  }

  async createCommunityComment(
    insertComment: InsertCommunityComment,
  ): Promise<CommunityComment> {
    const [comment] = await db
      .insert(communityComments)
      .values(insertComment)
      .returning();
    return comment;
  }

  // Template methods
  async getBelugaTemplates(): Promise<BelugaTemplate[]> {
    return await db.select().from(belugaTemplates);
  }

  async getBelugaTemplate(id: number): Promise<BelugaTemplate | undefined> {
    const [template] = await db
      .select()
      .from(belugaTemplates)
      .where(eq(belugaTemplates.id, id));
    return template;
  }

  async createBelugaTemplate(
    insertTemplate: InsertBelugaTemplate,
  ): Promise<BelugaTemplate> {
    const [template] = await db
      .insert(belugaTemplates)
      .values(insertTemplate)
      .returning();
    return template;
  }

  async updateBelugaTemplate(
    id: number,
    updates: Partial<InsertBelugaTemplate>,
  ): Promise<BelugaTemplate | undefined> {
    const [template] = await db
      .update(belugaTemplates)
      .set(updates)
      .where(eq(belugaTemplates.id, id))
      .returning();
    return template;
  }

  async deleteBelugaTemplate(id: number): Promise<boolean> {
    const result = await db
      .delete(belugaTemplates)
      .where(eq(belugaTemplates.id, id));
    return (result.rowCount || 0) > 0;
  }

  async reorderBelugaTemplates(templateIds: number[]): Promise<boolean> {
    for (let i = 0; i < templateIds.length; i++) {
      await db
        .update(belugaTemplates)
        .set({ sortOrder: i })
        .where(eq(belugaTemplates.id, templateIds[i]));
    }
    return true;
  }

  // Order Item methods
  async getOrderItems(orderId: number): Promise<OrderItem[]> {
    return await db
      .select()
      .from(orderItems)
      .where(eq(orderItems.orderId, orderId));
  }

  async createOrderItem(insertOrderItem: InsertOrderItem): Promise<OrderItem> {
    const [orderItem] = await db
      .insert(orderItems)
      .values(insertOrderItem)
      .returning();
    return orderItem;
  }

  // Payment methods
  async getPayments(orderId: number): Promise<Payment[]> {
    return await db
      .select()
      .from(payments)
      .where(eq(payments.orderId, orderId));
  }

  async createPayment(insertPayment: InsertPayment): Promise<Payment> {
    const [payment] = await db
      .insert(payments)
      .values(insertPayment)
      .returning();
    return payment;
  }

  async updatePaymentStatus(
    id: number,
    status: string,
  ): Promise<Payment | undefined> {
    const [payment] = await db
      .update(payments)
      .set({ status })
      .where(eq(payments.id, id))
      .returning();
    return payment;
  }

  // Coupon methods
  async getCoupons(): Promise<Coupon[]> {
    return await db.select().from(coupons);
  }

  async getCoupon(id: number): Promise<Coupon | undefined> {
    const [coupon] = await db.select().from(coupons).where(eq(coupons.id, id));
    return coupon;
  }

  async getCouponByCode(code: string): Promise<Coupon | undefined> {
    const [coupon] = await db
      .select()
      .from(coupons)
      .where(eq(coupons.code, code));
    return coupon;
  }

  async createCoupon(insertCoupon: InsertCoupon): Promise<Coupon> {
    const [coupon] = await db.insert(coupons).values(insertCoupon).returning();
    return coupon;
  }

  async updateCoupon(
    id: number,
    updates: Partial<InsertCoupon>,
  ): Promise<Coupon | undefined> {
    const [coupon] = await db
      .update(coupons)
      .set(updates)
      .where(eq(coupons.id, id))
      .returning();
    return coupon;
  }

  async deleteCoupon(id: number): Promise<boolean> {
    const result = await db.delete(coupons).where(eq(coupons.id, id));
    return (result.rowCount || 0) > 0;
  }

  // Admin Log methods
  async getAdminLogs(): Promise<AdminLog[]> {
    return await db.select().from(adminLogs);
  }

  async createAdminLog(insertAdminLog: InsertAdminLog): Promise<AdminLog> {
    const [adminLog] = await db
      .insert(adminLogs)
      .values(insertAdminLog)
      .returning();
    return adminLog;
  }

  // Goods Editor Design methods
  async getGoodsEditorDesigns(userId?: number): Promise<GoodsEditorDesign[]> {
    if (userId) {
      return await db
        .select()
        .from(goodsEditorDesigns)
        .where(eq(goodsEditorDesigns.userId, userId));
    }
    return await db.select().from(goodsEditorDesigns);
  }

  async getGoodsEditorDesignById(
    id: number,
  ): Promise<GoodsEditorDesign | undefined> {
    const [design] = await db
      .select()
      .from(goodsEditorDesigns)
      .where(eq(goodsEditorDesigns.id, id));
    return design;
  }

  async createGoodsEditorDesign(
    insertDesign: InsertGoodsEditorDesign,
  ): Promise<GoodsEditorDesign> {
    const [design] = await db
      .insert(goodsEditorDesigns)
      .values(insertDesign)
      .returning();
    return design;
  }

  async updateGoodsEditorDesign(
    id: number,
    updates: Partial<InsertGoodsEditorDesign>,
  ): Promise<GoodsEditorDesign | undefined> {
    const [design] = await db
      .update(goodsEditorDesigns)
      .set(updates)
      .where(eq(goodsEditorDesigns.id, id))
      .returning();
    return design;
  }

  async deleteGoodsEditorDesign(id: number): Promise<boolean> {
    const result = await db
      .delete(goodsEditorDesigns)
      .where(eq(goodsEditorDesigns.id, id));
    return (result.rowCount || 0) > 0;
  }

  // Inquiry methods
  async getInquiries(userId?: number): Promise<Inquiry[]> {
    if (userId) {
      return await db
        .select()
        .from(inquiries)
        .where(eq(inquiries.userId, userId));
    }
    return await db.select().from(inquiries);
  }

  async getInquiryById(id: number): Promise<Inquiry | undefined> {
    const [inquiry] = await db
      .select()
      .from(inquiries)
      .where(eq(inquiries.id, id));
    return inquiry;
  }

  async createInquiry(insertInquiry: InsertInquiry): Promise<Inquiry> {
    const [inquiry] = await db
      .insert(inquiries)
      .values(insertInquiry)
      .returning();
    return inquiry;
  }

  async updateInquiry(
    id: number,
    updates: Partial<InsertInquiry>,
  ): Promise<Inquiry | undefined> {
    await db
      .update(inquiries)
      .set(updates)
      .where(eq(inquiries.id, id));

    const [inquiry] = await db
      .select()
      .from(inquiries)
      .where(eq(inquiries.id, id));
    return inquiry;
  }

  // Point methods
  async getUserPoints(userId: number): Promise<number> {
    const [user] = await db
      .select({ points: users.points })
      .from(users)
      .where(eq(users.id, userId));
    return user?.points || 0;
  }

  async updateUserPoints(userId: number, points: number): Promise<User | undefined> {
    await db
      .update(users)
      .set({ points })
      .where(eq(users.id, userId));

    const [user] = await db
      .select()
      .from(users)
      .where(eq(users.id, userId));
    return user;
  }

  async createPointHistory(pointHistory: InsertPointHistory): Promise<PointHistory> {
    await db.insert(pointHistory).values(pointHistory);

    // Get the inserted record by finding the latest one for this user
    const [history] = await db
      .select()
      .from(pointHistory)
      .where(eq(pointHistory.userId, pointHistory.userId))
      .orderBy(sql`${pointHistory.createdAt} DESC`)
      .limit(1);

    return history;
  }

  async getPointHistory(userId: number, limit = 10): Promise<PointHistory[]> {
    return await db
      .select()
      .from(pointHistory)
      .where(eq(pointHistory.userId, userId))
      .orderBy(sql`${pointHistory.createdAt} DESC`)
      .limit(limit);
  }

  async earnPoints(userId: number, amount: number, source: string): Promise<{ user: User; history: PointHistory }> {
    const currentPoints = await this.getUserPoints(userId);
    const newBalance = currentPoints + amount;

    const user = await this.updateUserPoints(userId, newBalance);
    const history = await this.createPointHistory({
      userId,
      type: "earn",
      source,
      amount,
      balance: newBalance,
    });

    return { user: user!, history };
  }

  async usePoints(userId: number, amount: number, source: string): Promise<{ user: User; history: PointHistory } | null> {
    const currentPoints = await this.getUserPoints(userId);

    if (currentPoints < amount) {
      return null; // Not enough points
    }

    const newBalance = currentPoints - amount;

    const user = await this.updateUserPoints(userId, newBalance);
    const history = await this.createPointHistory({
      userId,
      type: "use",
      source,
      amount: -amount,
      balance: newBalance,
    });

    return { user: user!, history };
  }
}

// Temporarily use MemoryStorage until MySQL cloud database is properly configured
export const storage = new MemStorage();

