  -ms-user-select: none;
  user-select: none;
}

/* Allow selection for text input elements */
input, textarea, [contenteditable="true"] {
  -webkit-user-select: text;
  -moz-user-select: text;
  -ms-user-select: text;
  user-select: text;
}

/* Line clamp utility */
.line-clamp-2 {
  overflow: hidden;
  display: -webkit-box;
  -webkit-box-orient: vertical;
  -webkit-line-clamp: 2;
}

/* Popular Products Swiper Styles */
.popular-products-swiper {
  width: 100%;
