}

.popular-products-swiper .swiper-button-prev:after,
.popular-products-swiper .swiper-button-next:after {
  font-size: 20px;
  font-weight: 600;
}

.popular-products-swiper .swiper-button-prev {
  left: 10px;
}

.popular-products-swiper .swiper-button-next {
  right: 10px;
}

@media (max-width: 768px) {
  .popular-products-swiper .swiper-button-prev,
  .popular-products-swiper .swiper-button-next {
    display: none;
  }
}

/* Mobile Menu Slide Animation */
@keyframes slideInFromRight {
  from {
    transform: translateX(100%);
    opacity: 0;
  }
  to {
    transform: translateX(0);
    opacity: 1;
  }
}

@keyframes slideOutToRight {
  from {
    transform: translateX(0);
    opacity: 1;
  }
  to {
    transform: translateX(100%);
    opacity: 0;
  }
}

.mobile-menu-slide-in {
  animation: slideInFromRight 0.3s ease-out forwards;
}

.mobile-menu-slide-out {
  animation: slideOutToRight 0.3s ease-in forwards;
}

/* Pinto Editor Colors */
:root {
  --mint: 172 100% 38%;
  --mint-foreground: 0 0% 100%;
}

.mint {
  background-color: #00C19D;
}

.bg-mint {
  background-color: #00C19D;
}

.text-mint {
  color: #00C19D;
}

.border-mint {
  border-color: #00C19D;
}

.ring-mint {
  --tw-ring-color: #00C19D;
}

/* ì• ë‹ˆë©”ì´ì…˜ë“¤ */
.scroll-button {
  animation: bounce 2s infinite;
}

@keyframes bounce {
  0%, 20%, 50%, 80%, 100% {
    transform: translateY(0);
  }
  40% {
    transform: translateY(-10px);
  }
  60% {
    transform: translateY(-5px);
  }
}

.fade-in {
  opacity: 0;
