import React, { useState } from 'react'
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card'
import { Button } from '@/components/ui/button'
import { Textarea } from '@/components/ui/textarea'
import { Separator } from '@/components/ui/separator'
import { MessageSquare, User, Send, Edit2, Trash2, Save, X } from 'lucide-react'
import { Link } from 'wouter'
import { useSupabaseAuth } from '@/components/SupabaseProvider'
import { 
  usePostComments, 
  useCreatePostComment, 
  useUpdatePostComment, 
  useDeletePostComment,
  formatPostDate 
} from '@/hooks/useCommunityPosts'

interface CommentSectionProps {
  postId: string
  commentsCount?: number
}

interface CommentItemProps {
  comment: any
  currentUser: any
  onEdit: (commentId: string, newContent: string) => void
  onDelete: (commentId: string) => void
  isEditing: boolean
  onStartEdit: () => void
  onCancelEdit: () => void
}

const CommentItem: React.FC<CommentItemProps> = ({
  comment,
  currentUser,
  onEdit,
  onDelete,
  isEditing,
  onStartEdit,
  onCancelEdit
}) => {
  const [editContent, setEditContent] = useState(comment.content)
  const isOwner = currentUser && comment.user_id === currentUser.id

  const handleSaveEdit = () => {
    if (editContent.trim() !== comment.content) {
      onEdit(comment.id, editContent.trim())
    }
    onCancelEdit()
  }

  const handleCancelEdit = () => {
    setEditContent(comment.content)
    onCancelEdit()
  }

  return (
    <div className="flex items-start space-x-3 py-4">
      <div className="w-8 h-8 bg-gray-600 rounded-full flex items-center justify-center flex-shrink-0">
        <User className="w-4 h-4 text-gray-300" />
      </div>
      <div className="flex-1 min-w-0">
        <div className="flex items-center space-x-2 mb-1">
          <span className="font-medium text-white text-sm">
            {comment.users?.username || comment.users?.email || 'ìµëª…'}
          </span>
          <span className="text-xs text-gray-400">
            {formatPostDate(comment.created_at)}
          </span>
          {isOwner && !isEditing && (
            <div className="flex items-center space-x-1 ml-auto">
              <Button
                variant="ghost"
                size="sm"
                onClick={onStartEdit}
                className="text-gray-400 hover:text-white hover:bg-gray-700 p-1 h-auto"
              >
                <Edit2 className="w-3 h-3" />
              </Button>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => onDelete(comment.id)}
                className="text-red-400 hover:text-red-300 hover:bg-red-900/20 p-1 h-auto"
              >
                <Trash2 className="w-3 h-3" />
              </Button>
            </div>
          )}
        </div>

        {isEditing ? (
          <div className="space-y-2">
            <Textarea
              value={editContent}
              onChange={(e) => setEditContent(e.target.value)}
              className="bg-[#1a1a1a] border-gray-600 text-white placeholder-gray-400 text-sm"
              rows={3}
            />
            <div className="flex items-center space-x-2">
              <Button
                size="sm"
                onClick={handleSaveEdit}
                disabled={!editContent.trim()}
                className="bg-blue-600 hover:bg-blue-700 text-xs"
              >
                <Save className="w-3 h-3 mr-1" />
                ì €ì¥
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={handleCancelEdit}
                className="text-white border-gray-600 hover:bg-gray-700 text-xs"
              >
                <X className="w-3 h-3 mr-1" />
                ì·¨ì†Œ
              </Button>
            </div>
          </div>
        ) : (
