  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Globe } from "lucide-react";
import { NicknameSetupDialog } from "@/components/NicknameSetupDialog";

export const Header = () => {
  const [location, setLocation] = useLocation();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isSearchModalOpen, setIsSearchModalOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [wishlistCount, setWishlistCount] = useState(0);
  const [showNicknameDialog, setShowNicknameDialog] = useState(false);
  const { user: localUser, logout: localLogout } = useAuth();
  const { toast } = useToast();
  const { itemCount } = useCart();
  const { unreadCount } = useNotifications();
  const { language, setLanguage, t } = useLanguage();

  // Always use local auth for now - Supabase integration can be enabled later
  const currentUser = localUser;
  const isLoading = false;

  // Get wishlist count from localStorage
  const getWishlistCount = () => {
    try {
      const wishlist = JSON.parse(localStorage.getItem("wishlist") || "[]");
      return wishlist.length;
    } catch {
      return 0;
    }
  };

  // Update wishlist count when component mounts and when storage changes
  useEffect(() => {
    setWishlistCount(getWishlistCount());

    const handleStorageChange = () => {
      setWishlistCount(getWishlistCount());
    };

    window.addEventListener("storage", handleStorageChange);
    window.addEventListener("wishlist-updated", handleStorageChange);

    return () => {
      window.removeEventListener("storage", handleStorageChange);
      window.removeEventListener("wishlist-updated", handleStorageChange);
    };
  }, []);

  // Check for nickname requirement when user logs in
  useEffect(() => {
    if (currentUser && !currentUser.nickname) {
      setShowNicknameDialog(true);
    }
  }, [currentUser]);

  // Handle search
  const handleSearch = (e: React.FormEvent) => {
    e.preventDefault();
    if (searchQuery.trim()) {
      setLocation(`/search?q=${encodeURIComponent(searchQuery.trim())}`);
      setSearchQuery("");
      setIsMobileMenuOpen(false);
    }
  };

  const handleSearchInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setSearchQuery(e.target.value);
  };

  const navItems = [
    { name: t({ ko: "í™ˆ", en: "Home", ja: "ãƒ›ãƒ¼ãƒ ", zh: "é¦–é¡µ" }), href: "/" },
    {
      name: t({ ko: "ìƒí’ˆ", en: "Products", ja: "å•†å“", zh: "äº§å“" }),
      href: "/products",
    },
    {
      name: t({
        ko: "ì»¤ë®¤ë‹ˆí‹°",
        en: "Community",
        ja: "ã‚³ãƒŸãƒ¥ãƒ‹ãƒ†ã‚£",
        zh: "ç¤¾åŒº",
      }),
      href: "/community",
    },
  ];

