import { Badge } from "@/components/ui/badge";
import { Skeleton } from "@/components/ui/skeleton";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { ChevronLeft, ChevronRight, Package, AlertCircle } from "lucide-react";
import { supabase, isSupabaseConfigured } from "@/lib/supabase";
import { useToast } from "@/hooks/use-toast";
import FavoriteButton from "@/components/FavoriteButton";
import { AddToCartButton } from "@/components/AddToCartButton";

interface Product {
  id: string;
  name: string;
  name_ko: string;
  base_price: number;
  stock_quantity: number;
  is_featured: boolean;
  is_available: boolean;
  image_url?: string;
  category_id: string;
  created_at: string;
}

interface ProductsGridProps {
  pageSize?: number;
}

const ProductsGrid: React.FC<ProductsGridProps> = ({ pageSize = 12 }) => {
  const [products, setProducts] = useState<Product[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [totalCount, setTotalCount] = useState(0);
  const { toast } = useToast();

  const totalPages = Math.ceil(totalCount / pageSize);

  const fetchProducts = async (page: number) => {
    if (!isSupabaseConfigured) {
      setError("Supabaseê°€ êµ¬ì„±ë˜ì§€ ì•Šì•˜ìŠµë‹ˆë‹¤. í™˜ê²½ ë³€ìˆ˜ë¥¼ ì„¤ì •í•´ì£¼ì„¸ìš”.");
      setLoading(false);
      return;
    }

    try {
      setLoading(true);
      setError(null);

      const startIndex = (page - 1) * pageSize;
      const endIndex = startIndex + pageSize - 1;

      // Fetch products with pagination - only approved and active products
      const {
        data,
        error: fetchError,
        count,
      } = await supabase
        .from("products")
        .select("*", { count: "exact" })
        .eq("is_approved", true)
        .eq("is_active", true)
        .eq("is_active", true)
        .eq("is_approved", true)
        .range(startIndex, endIndex)
        .order("created_at", { ascending: false });

      if (fetchError) {
        throw fetchError;
      }

      setProducts(data || []);
      setTotalCount(count || 0);

      if (data && data.length > 0) {
        toast({
          title: "ìƒí’ˆ ë¡œë“œ ì™„ë£Œ",
