import { supabase } from "./supabase";
import type {
  Product,
  Category,
  User,
  Order,
  OrderItem,
  Review,
  Community,
  Event,
  Template,
  AdditionalService,
  Wishlist,
  Cart,
} from "./supabase";

// Products API
export const fetchProducts = async (options?: {
  categoryId?: string;
  featured?: boolean;
  available?: boolean;
  limit?: number;
  offset?: number;
}) => {
  let query = supabase.from("products").select(`
    *,
    categories(name, name_ko),
    reviews(rating, id)
  `);

  if (options?.categoryId) {
    query = query.eq("category_id", options.categoryId);
  }

  if (options?.featured !== undefined) {
    query = query.eq("is_featured", options.featured);
  }

  if (options?.available !== undefined) {
    query = query.eq("is_available", options.available);
  }

  if (options?.limit) {
    query = query.limit(options.limit);
  }

  if (options?.offset) {
    query = query.range(
      options.offset,
      options.offset + (options.limit || 10) - 1,
    );
  }

  query = query.order("created_at", { ascending: false });

  const { data, error } = await query;

  if (error) {
    console.error("Error fetching products:", error);
    throw error;
  }

  return data;
};

export const fetchProductById = async (id: string) => {
  const { data, error } = await supabase
    .from("products")
    .select(
      `
      *,
      categories(name, name_ko),
      reviews(*, users(username, avatar_url))
    `,
    )
    .eq("id", id)
    .single();

  if (error) {
    console.error("Error fetching product:", error);
    throw error;
  }

  return data;
};

export const fetchProductsByCategory = async (categoryId: string) => {
  const { data, error } = await supabase
    .from("products")
    .select(
      `
      *,
      categories(name, name_ko),
      reviews(rating, id)
    `,
    )
    .eq("category_id", categoryId)
    .eq("is_available", true)
    .order("created_at", { ascending: false });

  if (error) {
    console.error("Error fetching products by category:", error);
    throw error;
  }

  return data;
};

export const fetchFeaturedProducts = async () => {
  const { data, error } = await supabase
    .from("products")
    .select(
      `
      *,
      categories(name, name_ko),
      reviews(rating, id)
    `,
    )
    .eq("is_featured", true)
    .eq("is_available", true)
    .order("created_at", { ascending: false });

  if (error) {
    console.error("Error fetching featured products:", error);
    throw error;
  }

  return data;
};

// Product Reviews API
export const fetchProductReviews = async (productId: string) => {
  const { data, error } = await supabase
    .from("product_reviews")
    .select(
      `
      *,
      users(username, email, avatar_url)
    `,
    )
    .eq("product_id", productId)
    .order("created_at", { ascending: false });

  if (error) {
    console.error("Error fetching product reviews:", error);
    throw error;
  }

  return data;
};

export const createProductReview = async (review: {
  user_id: string;
  product_id: string;
  rating: number;
  review_text: string;
}) => {
  const { data, error } = await supabase
    .from("product_reviews")
    .insert([review])
    .select(
      `
      *,
      users(username, email, avatar_url)
    `,
    )
    .single();

  if (error) {
    console.error("Error creating product review:", error);
    throw error;
  }

  return data;
};

export const updateProductReview = async (
  reviewId: string,
  updates: {
    rating?: number;
    review_text?: string;
  },
) => {
  const { data, error } = await supabase
    .from("product_reviews")
    .update(updates)
    .eq("id", reviewId)
    .select(
      `
      *,
      users(username, email, avatar_url)
    `,
    )
    .single();

  if (error) {
    console.error("Error updating product review:", error);
    throw error;
  }

  return data;
};

export const deleteProductReview = async (reviewId: string) => {
  const { error } = await supabase
    .from("product_reviews")
    .delete()
    .eq("id", reviewId);

  if (error) {
    console.error("Error deleting product review:", error);
    throw error;
  }

  return true;
};

export const fetchUserReviewForProduct = async (
  userId: string,
  productId: string,
) => {
  const { data, error } = await supabase
    .from("product_reviews")
    .select(
      `
      *,
      users(username, email, avatar_url)
    `,
    )
    .eq("user_id", userId)
    .eq("product_id", productId)
    .single();

  if (error && error.code !== "PGRST116") {
    // PGRST116 is "not found" error
    console.error("Error fetching user review:", error);
    throw error;
  }

  return data;
};

// Delivery Tracking API
export const fetchDeliveryTracking = async (orderId: string) => {
  const { data, error } = await supabase
    .from("delivery_tracking")
    .select("*")
    .eq("order_id", orderId)
    .order("last_updated", { ascending: false })
    .limit(1)
    .single();

  if (error && error.code !== "PGRST116") {
    // PGRST116 is "not found" error
    console.error("Error fetching delivery tracking:", error);
    throw error;
  }

  return data;
};

export const createDeliveryTracking = async (tracking: {
  order_id: string;
  courier?: string;
  tracking_number?: string;
  status: string;
  estimated_delivery?: string;
}) => {
  const { data, error } = await supabase
    .from("delivery_tracking")
    .insert([
      {
        ...tracking,
        last_updated: new Date().toISOString(),
      },
    ])
    .select()
    .single();

  if (error) {
    console.error("Error creating delivery tracking:", error);
    throw error;
  }

  return data;
};

export const updateDeliveryTracking = async (
  trackingId: string,
  updates: {
    courier?: string;
    tracking_number?: string;
    status?: string;
    estimated_delivery?: string;
  },
) => {
  const { data, error } = await supabase
    .from("delivery_tracking")
    .update({
      ...updates,
