import { supabase } from './supabase'

export type BucketName = 'custom-designs' | 'post-images' | 'product-images'

// íŒŒì¼ ì—…ë¡œë“œ í•¨ìˆ˜
export async function uploadFile(
  bucketName: BucketName,
  file: File,
  fileName?: string
): Promise<{ url: string; path: string } | null> {
  try {
    const fileExt = file.name.split('.').pop()
    const finalFileName = fileName || `${Date.now()}-${Math.random().toString(36).substring(2)}.${fileExt}`

    const { data, error } = await supabase.storage
      .from(bucketName)
      .upload(finalFileName, file, {
        cacheControl: '3600',
        upsert: false
      })

    if (error) {
      console.error('íŒŒì¼ ì—…ë¡œë“œ ì‹¤íŒ¨:', error)
      return null
    }

    // ê³µê°œ URL ê°€ì ¸ì˜¤ê¸°
    const { data: { publicUrl } } = supabase.storage
      .from(bucketName)
      .getPublicUrl(finalFileName)

    console.log('íŒŒì¼ ì—…ë¡œë“œ ì„±ê³µ:', publicUrl)
    return {
      url: publicUrl,
      path: data.path
    }
  } catch (error) {
    console.error('íŒŒì¼ ì—…ë¡œë“œ ì¤‘ ì˜¤ë¥˜ ë°œìƒ:', error)
    return null
  }
}

// íŒŒì¼ ì‚­ì œ í•¨ìˆ˜
export async function deleteFile(bucketName: BucketName, filePath: string): Promise<boolean> {
  try {
    const { error } = await supabase.storage
      .from(bucketName)
      .remove([filePath])

    if (error) {
      console.error('íŒŒì¼ ì‚­ì œ ì‹¤íŒ¨:', error)
      return false
    }

    console.log('íŒŒì¼ ì‚­ì œ ì„±ê³µ:', filePath)
    return true
  } catch (error) {
    console.error('íŒŒì¼ ì‚­ì œ ì¤‘ ì˜¤ë¥˜ ë°œìƒ:', error)
    return false
  }
}

// ë‹¤ì¤‘ íŒŒì¼ ì—…ë¡œë“œ í•¨ìˆ˜
export async function uploadMultipleFiles(
  bucketName: BucketName,
  files: File[]
): Promise<{ url: string; path: string }[]> {
  const uploadPromises = files.map(file => uploadFile(bucketName, file))
  const results = await Promise.all(uploadPromises)

  return results.filter(result => result !== null) as { url: string; path: string }[]
}

// íŒŒì¼ URL ê°€ì ¸ì˜¤ê¸° í•¨ìˆ˜
export function getFileUrl(bucketName: BucketName, filePath: string): string {
  const { data: { publicUrl } } = supabase.storage
    .from(bucketName)
    .getPublicUrl(filePath)

  return publicUrl
}

// íŒŒì¼ í¬ê¸° ì œí•œ ì²´í¬ í•¨ìˆ˜
export function validateFileSize(file: File, maxSizeMB: number = 10): boolean {
  const maxSizeBytes = maxSizeMB * 1024 * 1024
  return file.size <= maxSizeBytes
}

// íŒŒì¼ íƒ€ì… ì²´í¬ í•¨ìˆ˜
export function validateFileType(file: File, allowedTypes: string[] = ['image/jpeg', 'image/png', 'image/webp', 'image/gif']): boolean {
  return allowedTypes.includes(file.type)
}

// íŒŒì¼ ì—…ë¡œë“œ ì „ ê²€ì¦ í•¨ìˆ˜
export function validateFile(file: File, maxSizeMB: number = 10): { isValid: boolean; error?: string } {
  if (!validateFileType(file)) {
    return { isValid: false, error: 'ì§€ì›í•˜ì§€ ì•ŠëŠ” íŒŒì¼ í˜•ì‹ì…ë‹ˆë‹¤. (JPEG, PNG, WebP, GIFë§Œ í—ˆìš©)' }
  }

  if (!validateFileSize(file, maxSizeMB)) {
    return { isValid: false, error: `íŒŒì¼ í¬ê¸°ê°€ ${maxSizeMB}MBë¥¼ ì´ˆê³¼í•©ë‹ˆë‹¤.` }
  }

  return { isValid: true }
}

// ì´ë¯¸ì§€ ë¦¬ì‚¬ì´ì§• í•¨ìˆ˜ (ì„ íƒì‚¬í•­)
export function resizeImage(file: File, maxWidth: number = 1920, maxHeight: number = 1080, quality: number = 0.8): Promise<File> {
  return new Promise((resolve) => {
    const canvas = document.createElement('canvas')
    const ctx = canvas.getContext('2d')
    const img = new Image()

    img.onload = () => {
      const { width, height } = img

      // ë¹„ìœ¨ ê³„ì‚°
      const ratio = Math.min(maxWidth / width, maxHeight / height)
      const newWidth = width * ratio
      const newHeight = height * ratio

      canvas.width = newWidth
      canvas.height = newHeight

      // ì´ë¯¸ì§€ ê·¸ë¦¬ê¸°
      ctx?.drawImage(img, 0, 0, newWidth, newHeight)

      // Blobìœ¼ë¡œ ë³€í™˜
      canvas.toBlob((blob) => {
        if (blob) {
          const resizedFile = new File([blob], file.name, {
            type: file.type,
            lastModified: Date.now()
          })
          resolve(resizedFile)
        } else {
          resolve(file)
        }
      }, file.type, quality)
    }

    img.src = URL.createObjectURL(file)
  })
}

