  const { setUser, redirectPath, setRedirectPath } = useAuth();
  const [isLoading, setIsLoading] = useState(false);
  const { t } = useLanguage();
  const [, setLocation] = useLocation();

  // URL íŒŒë¼ë¯¸í„°ì—ì„œ redirect_to ì½ê¸° ë° ì†Œì…œ ë¡œê·¸ì¸ í† í° ì²˜ë¦¬
  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const redirectTo = urlParams.get('redirect_to');
    const token = urlParams.get('token');
    const socialLogin = urlParams.get('social_login');

    if (redirectTo) {
      setRedirectPath(decodeURIComponent(redirectTo));
    }

    // ì†Œì…œ ë¡œê·¸ì¸ ì„±ê³µ ì‹œ í† í° ì²˜ë¦¬
    if (token && socialLogin === 'success') {
      handleSocialLoginSuccess(token);
    }
  }, [setRedirectPath]);

  const handleSocialLoginSuccess = async (token: string) => {
    try {
      // JWT í† í°ì„ ì €ì¥
      localStorage.setItem("token", token);

      // í† í°ìœ¼ë¡œ ì‚¬ìš©ì ì •ë³´ ê°€ì ¸ì˜¤ê¸°
      const response = await fetch('/api/auth/me', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (response.ok) {
        const userData = await response.json();
        const mappedUser = {
          id: userData.id,
          name: userData.full_name || userData.username,
          username: userData.username,
          email: userData.email,
          points: userData.points_balance || 0,
          coupons: 0,
          totalOrders: 0,
          totalSpent: userData.total_spent || 0,
          isAdmin: userData.is_admin || false,
          firstName: userData.full_name ? userData.full_name.split(' ')[0] : "",
          lastName: userData.full_name ? userData.full_name.split(' ').slice(1).join(' ') : "",
        };

        setUser(mappedUser);
        localStorage.setItem("user", JSON.stringify(mappedUser));

        // URLì—ì„œ í† í° íŒŒë¼ë¯¸í„° ì œê±°
        window.history.replaceState({}, document.title, window.location.pathname);

        // ë¦¬ë‹¤ì´ë ‰íŠ¸
        const targetPath = redirectPath || "/";
        setRedirectPath(null);
        setLocation(targetPath);
      }
    } catch (error) {
      console.error('ì†Œì…œ ë¡œê·¸ì¸ ì²˜ë¦¬ ì‹¤íŒ¨:', error);
      setError('ë¡œê·¸ì¸ ì²˜ë¦¬ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.');
    }
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError("");
    setIsLoading(true);

    try {
      console.log('ë¡œê·¸ì¸ ì‹œë„:', formData);

      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          username: formData.username,
          password: formData.password,
        }),
      });

      const data = await response.json();
      console.log('ë¡œê·¸ì¸ ì‘ë‹µ:', data);

      if (response.ok && data) {
        const mappedUser = {
          id: data.id,
          name: data.first_name && data.last_name ? `${data.first_name} ${data.last_name}` : data.username,
          username: data.username,
          email: data.email,
          points: 0,
          coupons: 0,
          totalOrders: 0,
          totalSpent: 0,
          isAdmin: data.isAdmin || false,
          firstName: data.first_name || "",
          lastName: data.last_name || "",
