        setError(data.message || t({
          ko: "ì•„ì´ë”” ë˜ëŠ” ë¹„ë°€ë²ˆí˜¸ê°€ ì˜ëª»ë˜ì—ˆìŠµë‹ˆë‹¤.",
          en: "Invalid username or password.",
        }));
      }
    } catch (err) {
      console.error('ë¡œê·¸ì¸ ì˜¤ë¥˜:', err);
      setError(
        t({
          ko: "ë¡œê·¸ì¸ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.",
          en: "An error occurred during login.",
        }),
      );
    } finally {
      setIsLoading(false);
    }
  };

  const handleSnsLogin = (provider: "kakao" | "naver") => {
    // ì†Œì…œ ë¡œê·¸ì¸ API ì—”ë“œí¬ì¸íŠ¸ë¡œ ë¦¬ë‹¤ì´ë ‰íŠ¸
    window.location.href = `/auth/${provider}`;
  };

