              </div>

              <div className="space-y-2">
                <label
                  htmlFor="password"
                  className="block text-sm font-medium text-gray-700 dark:text-gray-300"
                >
                  {t({
                    ko: "ë¹„ë°€ë²ˆí˜¸",
                    en: "Password",
                    ja: "ãƒ‘ã‚¹ãƒ¯ãƒ¼ãƒ‰",
                    zh: "å¯†ç ",
                  })}
                </label>
                <div className="relative">
                  <Input
                    id="password"
                    type={showPassword ? "text" : "password"}
