import React, { useState } from "react";
import { useParams } from "wouter";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import {
  Package,
  CreditCard,
  Truck,
  MapPin,
  Clock,
  CheckCircle,
  AlertCircle,
  Printer,
  Calendar,
  DollarSign,
} from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { Link } from "wouter";
import { RefundRequestButton } from "@/components/RefundRequestButton";

interface Order {
  id: number;
  user_id: number;
  status: string;
  total_amount: number;
  created_at: string;
  updated_at: string;
  shipping_address: any;
  order_items: any[];
}

interface Payment {
  id: number;
  order_id: number;
  method: string;
  status: string;
  amount: number;
  created_at: string;
  transaction_id?: string;
}

interface ShippingInfo {
  id: number;
  order_id: number;
  recipient_name: string;
  recipient_phone: string;
  shipping_address: string;
  shipping_method: string;
  tracking_number?: string;
  shipping_status: string;
  created_at: string;
}

interface DeliveryTracking {
  id: number;
  order_id: number;
  courier_company: string;
  tracking_number: string;
  current_status: string;
  estimated_delivery: string;
  created_at: string;
}

interface PrintJob {
  id: number;
  order_id: number;
  status: string;
  print_details: any;
  created_at: string;
  completed_at?: string;
}

export default function OrderDetail() {
  const { id } = useParams();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [refundReason, setRefundReason] = useState("");

  const orderId = id ? parseInt(id) : 0;

  // Fetch order data
  const {
    data: order,
    isLoading: orderLoading,
    error: orderError,
  } = useQuery<Order>({
    queryKey: ["order", orderId],
    queryFn: async () => {
      const response = await fetch(`/api/orders/${orderId}`);
      if (!response.ok) {
        throw new Error("ì£¼ë¬¸ ì •ë³´ë¥¼ ë¶ˆëŸ¬ì˜¤ëŠ”ë° ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤.");
      }
      return response.json();
    },
    enabled: !!orderId,
  });

  // Fetch payment data
  const { data: payment, isLoading: paymentLoading } = useQuery<Payment>({
    queryKey: ["payment", orderId],
    queryFn: async () => {
      const response = await fetch(`/api/payments/order/${orderId}`);
      if (!response.ok) {
        return null; // Payment info might not exist
      }
      return response.json();
    },
    enabled: !!orderId,
  });

  // Fetch shipping info
  const { data: shipping, isLoading: shippingLoading } = useQuery<ShippingInfo>(
    {
      queryKey: ["shipping", orderId],
      queryFn: async () => {
        const response = await fetch(`/api/shipping/${orderId}`);
        if (!response.ok) {
          return null; // Shipping info might not exist
        }
        return response.json();
      },
      enabled: !!orderId,
    },
  );

  // Fetch delivery tracking
  const { data: tracking, isLoading: trackingLoading } =
    useQuery<DeliveryTracking>({
      queryKey: ["delivery-tracking", orderId],
      queryFn: async () => {
        const response = await fetch(`/api/delivery-tracking/${orderId}`);
        if (!response.ok) {
          return null; // Tracking info might not exist
        }
        return response.json();
      },
      enabled: !!orderId,
    });

