}

interface FilterState {
  searchText: string;
  selectedCategories: string[];
  minPrice: number;
  maxPrice: number;
  sortBy: string;
  viewMode: 'grid' | 'list';
  showFeaturedOnly: boolean;
  showActiveOnly: boolean;
}

export default function ProductSearchPage() {
  const [location, setLocation] = useLocation();
  const { toast } = useToast();
  const { language, t } = useLanguage();

  // Get search params from URL
  const searchParams = new URLSearchParams(location.split('?')[1] || '');
  const initialSearch = searchParams.get('q') || '';
  const initialCategory = searchParams.get('category') || '';

  // Filter state
  const [filters, setFilters] = useState<FilterState>({
    searchText: initialSearch,
    selectedCategories: initialCategory ? [initialCategory] : [],
    minPrice: 0,
    maxPrice: 100000,
    sortBy: 'name',
    viewMode: 'grid',
    showFeaturedOnly: false,
    showActiveOnly: true,
  });

  const [showFilters, setShowFilters] = useState(false);
  const [isSearching, setIsSearching] = useState(false);

  // Categories query
  const { data: categories, isLoading: categoriesLoading } = useQuery({
    queryKey: ['categories'],
    queryFn: async () => {
      const { data, error } = await supabase
        .from('categories')
        .select('*')
        .eq('is_active', true)
        .order('name');

      if (error) {
        console.error('Error fetching categories:', error);
        throw error;
      }

      return data;
    }
  });

  // Products query with filters
  const { data: products, isLoading: productsLoading, refetch } = useQuery({
    queryKey: ['products', 'search', filters],
    queryFn: async () => {
      setIsSearching(true);

      let query = supabase
        .from('products')
        .select(`
          *,
          categories (
            id,
            name,
            name_ko
          )
        `);

      // Apply search filter
      if (filters.searchText.trim()) {
        query = query.or(
          `name.ilike.%${filters.searchText}%,name_ko.ilike.%${filters.searchText}%,description.ilike.%${filters.searchText}%,description_ko.ilike.%${filters.searchText}%`
        );
      }

      // Apply category filter
      if (filters.selectedCategories.length > 0) {
        query = query.in('category_id', filters.selectedCategories);
      }

      // Apply price range filter
      if (filters.minPrice > 0 || filters.maxPrice < 100000) {
        query = query.gte('base_price', filters.minPrice).lte('base_price', filters.maxPrice);
      }

      // Apply featured filter
      if (filters.showFeaturedOnly) {
        query = query.eq('is_featured', true);
      }

      // Apply active filter
      if (filters.showActiveOnly) {
        query = query.eq('is_active', true);
      }

      // Apply sorting
      switch (filters.sortBy) {
        case 'price_low':
          query = query.order('base_price', { ascending: true });
          break;
        case 'price_high':
          query = query.order('base_price', { ascending: false });
          break;
        case 'newest':
          query = query.order('created_at', { ascending: false });
          break;
        case 'featured':
          query = query.order('is_featured', { ascending: false }).order('name');
          break;
        default:
          query = query.order('name');
      }

      const { data, error } = await query;

      setIsSearching(false);

      if (error) {
        console.error('Error fetching products:', error);
        throw error;
      }

      return data || [];
    },
    enabled: true
