} from "@/components/ui/dropdown-menu";
import type { Product } from "@shared/schema";

export default function SearchResults() {
  const { t } = useLanguage();
  const [location, setLocation] = useLocation();
  const [searchQuery, setSearchQuery] = useState("");
  const [sortBy, setSortBy] = useState("latest");
  const [selectedCategory, setSelectedCategory] = useState("all");
  const [priceRange, setPriceRange] = useState("all");
  const [searchHistory, setSearchHistory] = useState<string[]>([]);

  // URLì—ì„œ ê²€ìƒ‰ì–´ ì¶”ì¶œ
  const urlParams = new URLSearchParams(location.split('?')[1] || '');
  const initialQuery = urlParams.get('query') || urlParams.get('keyword') || '';

  useEffect(() => {
    if (initialQuery) {
      setSearchQuery(initialQuery);
      // ê²€ìƒ‰ ê¸°ë¡ì— ì¶”ê°€
      setSearchHistory(prev => {
        const newHistory = [initialQuery, ...prev.filter(h => h !== initialQuery)].slice(0, 5);
        localStorage.setItem('searchHistory', JSON.stringify(newHistory));
        return newHistory;
      });
    }

    // ë¡œì»¬ ìŠ¤í† ë¦¬ì§€ì—ì„œ ê²€ìƒ‰ ê¸°ë¡ ë¡œë“œ
    const saved = localStorage.getItem('searchHistory');
    if (saved) {
      setSearchHistory(JSON.parse(saved));
    }
  }, [initialQuery]);

  // ìƒí’ˆ ë°ì´í„° ê°€ì ¸ì˜¤ê¸° (ê²€ìƒ‰ API ì‚¬ìš©)
  const { data: products = [], isLoading } = useQuery<Product[]>({
    queryKey: ["/api/products/search", searchQuery, selectedCategory, priceRange, sortBy],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (searchQuery) params.append('q', searchQuery);
      if (selectedCategory !== "all") params.append('category', selectedCategory);
      if (priceRange !== "all") params.append('priceRange', priceRange);
      if (sortBy) params.append('sortBy', sortBy);

      const response = await fetch(`/api/products/search?${params.toString()}`);
      if (!response.ok) {
        throw new Error('Failed to fetch search results');
      }
      return response.json();
    },
  });

  // ê²€ìƒ‰ ê²°ê³¼ëŠ” ë°±ì—”ë“œì—ì„œ ì´ë¯¸ í•„í„°ë§ë˜ì–´ ì˜´
  const sortedProducts = products;

  const handleSearch = (query: string) => {
    if (query.trim()) {
      setSearchQuery(query);
      setLocation(`/search?query=${encodeURIComponent(query)}`);
    }
  };

  const handleRemoveHistory = (item: string) => {
    const newHistory = searchHistory.filter(h => h !== item);
    setSearchHistory(newHistory);
    localStorage.setItem('searchHistory', JSON.stringify(newHistory));
  };

  const categories = [
    { id: "all", name: { ko: "ì „ì²´", en: "All", ja: "ã™ã¹ã¦", zh: "å…¨éƒ¨" } },
    { id: "1", name: { ko: "ì•„í¬ë¦´êµ¿ì¦ˆ", en: "Acrylic Goods", ja: "ã‚¢ã‚¯ãƒªãƒ«ã‚°ãƒƒã‚º", zh: "äºšå…‹åŠ›å•†å“" } },
    { id: "2", name: { ko: "ìš°ë“œêµ¿ì¦ˆ", en: "Wood Goods", ja: "ã‚¦ãƒƒãƒ‰ã‚°ãƒƒã‚º", zh: "æœ¨åˆ¶å•†å“" } },
    { id: "3", name: { ko: "ëœì•¼ë“œêµ¿ì¦ˆ", en: "Lanyard Goods", ja: "ãƒ©ãƒ³ãƒ¤ãƒ¼ãƒ‰ã‚°ãƒƒã‚º", zh: "æŒ‚ç»³å•†å“" } },
    { id: "4", name: { ko: "í¬ì¥/ë¶€ìì¬", en: "Packaging", ja: "ãƒ‘ãƒƒã‚±ãƒ¼ã‚¸", zh: "åŒ…è£…" } },
  ];

  const priceRanges = [
    { id: "all", name: { ko: "ì „ì²´ ê°€ê²©", en: "All Prices", ja: "ã™ã¹ã¦ã®ä¾¡æ ¼", zh: "æ‰€æœ‰ä»·æ ¼" } },
    { id: "under10", name: { ko: "1ë§Œì› ë¯¸ë§Œ", en: "Under â‚©10,000", ja: "1ä¸‡å††æœªæº€", zh: "1ä¸‡éŸ©å…ƒä»¥ä¸‹" } },
    { id: "10to30", name: { ko: "1ë§Œì› ~ 3ë§Œì›", en: "â‚©10,000 ~ â‚©30,000", ja: "1ä¸‡å†† ~ 3ä¸‡å††", zh: "1-3ä¸‡éŸ©å…ƒ" } },
    { id: "30to50", name: { ko: "3ë§Œì› ~ 5ë§Œì›", en: "â‚©30,000 ~ â‚©50,000", ja: "3ä¸‡å†† ~ 5ä¸‡å††", zh: "3-5ä¸‡éŸ©å…ƒ" } },
    { id: "over50", name: { ko: "5ë§Œì› ì´ìƒ", en: "Over â‚©50,000", ja: "5ä¸‡å††ä»¥ä¸Š", zh: "5ä¸‡éŸ©å…ƒä»¥ä¸Š" } },
  ];

  const sortOptions = [
    { id: "latest", name: { ko: "ìµœì‹ ìˆœ", en: "Latest", ja: "æœ€æ–°é †", zh: "æœ€æ–°" } },
    { id: "popular", name: { ko: "ì¸ê¸°ìˆœ", en: "Popular", ja: "äººæ°—é †", zh: "çƒ­é—¨" } },
    { id: "priceLow", name: { ko: "ë‚®ì€ ê°€ê²©ìˆœ", en: "Price: Low to High", ja: "ä¾¡æ ¼: å®‰ã„é †", zh: "ä»·æ ¼: ä½åˆ°é«˜" } },
    { id: "priceHigh", name: { ko: "ë†’ì€ ê°€ê²©ìˆœ", en: "Price: High to Low", ja: "ä¾¡æ ¼: é«˜ã„é †", zh: "ä»·æ ¼: é«˜åˆ°ä½" } },
    { id: "name", name: { ko: "ì´ë¦„ìˆœ", en: "Name", ja: "åå‰é †", zh: "åç§°" } },
  ];

  return (
    <Layout>
      <div className="min-h-screen bg-gray-50 dark:bg-[#1a1a1a]">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {/* ê²€ìƒ‰ í—¤ë” */}
          <div className="mb-8">
            <div className="flex items-center space-x-2 mb-4">
              <Search className="w-5 h-5 text-gray-500" />
              <h1 className="text-2xl font-bold text-gray-900 dark:text-white">
                {t({ ko: "ìƒí’ˆ ê²€ìƒ‰", en: "Product Search", ja: "å•†å“æ¤œç´¢", zh: "å•†å“æœç´¢" })}
              </h1>
            </div>

            {/* ê²€ìƒ‰ì°½ */}
            <div className="flex space-x-4 mb-6">
              <div className="flex-1 relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-500" />
                <Input
                  type="search"
                  placeholder={t({ ko: "ìƒí’ˆëª…ì„ ì…ë ¥í•˜ì„¸ìš”", en: "Enter product name", ja: "å•†å“åã‚’å…¥åŠ›", zh: "è¾“å…¥å•†å“åç§°" })}
                  className="pl-10 pr-4 h-12"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  onKeyPress={(e) => {
                    if (e.key === 'Enter') {
                      handleSearch(searchQuery);
                    }
                  }}
                />
              </div>
              <Button 
                onClick={() => handleSearch(searchQuery)}
                className="h-12 px-6"
              >
                {t({ ko: "ê²€ìƒ‰", en: "Search", ja: "æ¤œç´¢", zh: "æœç´¢" })}
              </Button>
            </div>

            {/* ê²€ìƒ‰ ê¸°ë¡ */}
            {searchHistory.length > 0 && (
              <div className="mb-4">
                <p className="text-sm text-gray-600 dark:text-gray-400 mb-2">
                  {t({ ko: "ìµœê·¼ ê²€ìƒ‰", en: "Recent Searches", ja: "æœ€è¿‘ã®æ¤œç´¢", zh: "æœ€è¿‘æœç´¢" })}
                </p>
                <div className="flex flex-wrap gap-2">
