import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/contexts/AuthContext";
import { Link, useLocation } from "wouter";
import {
  Package,
  Plus,
  Edit,
  Trash2,
  Eye,
  ShoppingCart,
  BarChart3,
  Users,
  AlertCircle,
  CheckCircle,
  Clock,
  TrendingUp,
  DollarSign,
  FileText,
} from "lucide-react";

interface Seller {
  id: number;
  shopName: string;
  businessNumber: string;
  contactEmail: string;
  contactPhone: string;
  address: string;
  bankAccount: string;
  bankName: string;
  isApproved: boolean;
  status: "pending" | "approved" | "rejected";
  createdAt: string;
}

interface Product {
  id: number;
  name: string;
  nameKo: string;
  description: string;
  descriptionKo: string;
  basePrice: number;
  categoryId: number;
  imageUrl: string;
  stock: number;
  isActive: boolean;
  isApproved: boolean;
  approvalDate: string | null;
  createdAt: string;
}
function ProductForm({
  product,
  onSubmit,
  isLoading,
}: {
  product?: Product | null;
  onSubmit: (data: Partial<Product>) => void;
  isLoading: boolean;
}) {
  const [formData, setFormData] = useState({
    name: product?.name || "",
    nameKo: product?.nameKo || "",
    description: product?.description || "",
    descriptionKo: product?.descriptionKo || "",
    basePrice: product?.basePrice?.toString() || "",
    categoryId: product?.categoryId?.toString() || "",
    imageUrl: product?.imageUrl || "",
    stock: product?.stock?.toString() || "",
    options: product?.options ? JSON.stringify(product.options, null, 2) : "",
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    let parsedOptions: any = null;
    if (formData.options) {
      try {
        parsedOptions = JSON.parse(formData.options);
      } catch {
        toast({
          title: "ì˜µì…˜ í˜•ì‹ ì˜¤ë¥˜",
          description: "ì˜µì…˜ JSONì„ í™•ì¸í•´ì£¼ì„¸ìš”.",
          variant: "destructive",
        });
        return;
      }
    }
    onSubmit({
      ...formData,
      basePrice: parseFloat(formData.basePrice),
      categoryId: parseInt(formData.categoryId),
      stock: parseInt(formData.stock) || 0,
      options: parsedOptions,
    });
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-4">
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <div>
          <Label htmlFor="editName">ìƒí’ˆëª… (ì˜ë¬¸) *</Label>
          <Input
            id="editName"
            value={formData.name}
            onChange={(e) => setFormData({ ...formData, name: e.target.value })}
          />
        </div>
        <div>
          <Label htmlFor="editNameKo">ìƒí’ˆëª… (í•œê¸€) *</Label>
          <Input
            id="editNameKo"
            value={formData.nameKo}
            onChange={(e) =>
              setFormData({ ...formData, nameKo: e.target.value })
            }
          />
        </div>
        <div>
          <Label htmlFor="editPrice">ê°€ê²© *</Label>
          <Input
            id="editPrice"
            type="number"
